import { useState } from "react";


function John({red,yel,on,off}){

    const{color,setColor}=useState(red)
    const{pas,setPas}=useState(on)

    const Upon=()=>{
        let a=pas==on ? off :on
        setPas(a)
        let b=color==red ? yel:red
        setColor(b)
    }
    return(
        <div style={{backgroundColor:color,width:"300px",height:"300px",borderRadius:"50%"}}>
            
            <button onClick={Upon}>{pas}</button>
        </div>
    )
}


export default John;
