import {useEffect,useState } from "react";
import { Link } from "react-router-dom";




function Cart(){

  const[data,setData]=useState([])
  const[total,setTotal]=useState(0)
  useEffect(()=>{
        fetch("http://localhost:3006/brand")
    .then((res) => {
      return res.json()
    })
    .then((resp) => {
      setData(resp)
    })
  },[])
  const Delete=(id)=>{
    fetch("http://localhost:3006/brand/"+id,{
        method:"DELETE"
    })
    window.location.reload()
   }
   const [value,setValue]=useState(1)

   const increment=()=>{
       setValue(value+1)
   }
 
   const decrement=()=>{
      if(value<=1){
        return false
      }
      setValue (value-1)
  }
   
   useEffect(() => {
    fetch("http://localhost:3006/brand")
        .then((res) => {
            return res.json()
        })
        .then((resp) => {
            setData(resp)
            const total=resp.reduce((acc,item)=>acc+parseFloat(item.price),0)
            setTotal(total)
        })
}, [])


    return(
        <div>
          <div style={{fontSize:"30px",marginTop:"30px",marginLeft:"50px",color:"red"}}>
            <Link to="/control">
              <a><i class="fa-solid fa-house"></i></a>
              </Link>
              <h2 style={{marginLeft:"500px"}}> Total Price:{total}</h2>
          </div>
 <div className="five">
            <table class="table">
  <thead>
    <tr>
      <th scope="col">Id</th>
      <th scope="col">IMAGE</th>
      <th scope="col">BRAND</th>
      <th scope="col">MODEL</th>
      <th scope="col">YEAR</th>
      <th scope="col">PRICE</th>
    </tr>
  </thead>
  <tbody class="table-group-divider">
    
    {data.map((one) => (
      <tr>
      <td scope="row">{one.id}</td>
      <td scope="row"><img src={one.img} style={{width:"100px",height:"150px"}} /></td>
      <td scope="row">{one.brand}</td>
      <td scope="row">{one.model}</td>
      <td scope="row">{one.year}</td>
      <td scope="row" style={{fontSize:"20px",color:""}}> ${one.price}</td>
      {/* <td>
      <a id="a" className="btn btn-success" onClick={()=>{Delete(one.id)}} >Remove</a>
      </td> */}
      <td>
      <a id="a" className="btn btn-danger" onClick={()=>{Delete(one.id)}} >Remove</a>
      </td>
      </tr>
    ))}
    
   
  </tbody>
</table>


          </div>        
        </div>
    )
}

export default Cart;
